import React, { useContext } from "react";
import AuthContext from "../context/auth";

const Header = () => {
    const auth = useContext(AuthContext);

    return (
        <header className="flex items-center p-5">
            <img src="/assets/Logo@3x.png" alt="logo" style={{
                height: "60px"
            }} />

            {auth.token !== null && (
                <button className="ms-auto">Logout</button>
            )}
        </header>
    );
}

const Footer = () => {
    return (
        <footer className="bg-slate-900 text-center p-3">
            <p className="text-white">All rights reserved. Shawormaa</p>
        </footer>
    );
}

const Layout = ({ children }) => {
    return (
        <>
            <Header /> 
            <main>
                {children}
            </main>
            <Footer />
        </>
    );
}

export default Layout;
