import React from "react";

const MenuList = ({ action = "add", menuItems, title }) => {
  return (
    <div className="flex flex-col gap-2 w-full">
      <h2 className="text-2xl text-slate-800">{title}</h2>

      {menuItems.map((i) => (
        <div
          key={i.id}
          className="w-full border border-slate-200 shadow-md flex items-center gap-2 px-4 py-2"
        >
          <div>{i.name}</div>
          <div className="ml-auto font-bold text-primary-dark">
            {i.price} EUR
          </div>
        </div>
      ))}
    </div>
  );
};

export default MenuList;
