import {BaseLayout} from "../../layout/BaseLayout";
import {Header} from "../../layout/Header";
import {TextField} from "../../components/form/TextField";
import {useState} from "react";
import {ReactComponent as SearchIcon} from "../../components/icons/search.svg";
import {Button} from "../../components/buttons/Button";
import axios from "axios";

/**
 * Search Page react component
 * @returns {null}
 * @constructor
 */
export function SearchPage() {
    const [query, setQuery] = useState("");
    const [restaurants, setRestaurants] = useState([]);
    const [showMore, setShowMore] = useState(false);

    async function fetchRestaurants(){
        const result = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/search?search=${query}`)
        setRestaurants(result.data.sort(() => Math.random() - 0.5));
    }

    function handleSearch(e){
        e.preventDefault();

        fetchRestaurants();
    }

    return <BaseLayout>
        <Header logoColor="dark"/>

        <div className="flex flex-col items-center gap-8">
            <form onSubmit={handleSearch} className="flex flex-row gap-2 w-full max-w-md">
                <TextField value={query} onChange={setQuery} placeholder="Search for restaurants" type="search" startIcon={<SearchIcon className="w-6 h-6 fill-gray-500" />} />
                <Button type="submit">
                    <SearchIcon className="w-8 h-8 fill-purple-500" />
                </Button>
            </form>
        </div>
    </BaseLayout>
}
