import React, { useCallback, useEffect, useState } from "react";
import MainLayout from "../layout/MainLayout";
import Input from "../components/ui/Input";
import search from "../assets/Search Icon.svg";
import ButtonSecondary from "../components/ui/ButtonSecondary";
import axios from "axios";
import Restaurant from "../components/Restaurant";
import { useDineEaseContext } from "../context/DineEaseContext";

/**
 * Index page
 */
const Index = () => {
  const { token } = useDineEaseContext();

  /**
   * Store the restaurants
   */
  const [restaurants, setRestaurants] = useState([]);

  /**
   * Searches for the restaurant and sets the state
   */
  const handleSearch = useCallback((query) => {
    const url = `/search${query ? `?query=${query}` : ""}`;
    axios.get(url).then((res) => setRestaurants(res.data));
  }, []);

  /**
   * Fetch the restaurants on page load
   */
  useEffect(() => handleSearch(), [handleSearch]);

  return (
    <MainLayout>
      <form className="flex items-center gap-2 mx-auto">
        <Input
          placeholder="Search for restaurant"
          icon={<img src={search} alt="Search Icon" />}
        />
        <ButtonSecondary fullWidth={false}>
          <img src={search} alt="Search Icon" />
        </ButtonSecondary>
      </form>

      <div className="grid grid-cols-3 px-16 gap-6">
        {restaurants.map((r) => (
          <Restaurant key={r.id} restaurant={r} />
        ))}
      </div>
    </MainLayout>
  );
};

export default Index;
