import {useOrder} from "../../hooks/useOrder";
import {Link} from "react-router-dom";
import {Button} from "../../components/Button";
import {useMemo} from "react";

/**
 * Component for displaying the summary of the order
 * @param restaurantId
 * @returns {JSX.Element}
 * @constructor
 */
export function OrderSummary({restaurantId}) {
    const {order} = useOrder();

    const total = useMemo(() => order.reduce((sum, item) => sum + item.price, 0), [order]);

    return <Link to={`/restaurants/${restaurantId}/order`}><Button variant="secondary">
        Finish order ({total.toFixed(2)} EUR)
    </Button></Link>
}
