import React, { useEffect, useState } from "react";
import MainLayout from "../layout/MainLayout";
import { useParams } from "react-router-dom";
import restaurantImage from "../assets/restaurant.webp";
import axios from "axios";
import { useDineEaseContext } from "../context/DineEaseContext";

/**
 * Page for displaying a restaurant
 */
const RestaurantPage = () => {
  const { token } = useDineEaseContext();

  /**
   * Get the ID from the params
   */
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState();

  /**
   * Fetch the restaurant on page load
   */
  useEffect(() => {
    axios.get(`/restaurant/${id}`).then((res) => setRestaurant(res.data));
  }, [id]);

  if (!restaurant) return null;

  return (
    <MainLayout>
      <img
        src={restaurantImage}
        className="absolute w-full top-0 left-0 right-0"
        alt="Restaurant"
      />
    </MainLayout>
  );
};

export default RestaurantPage;
