import {useParams} from "react-router-dom";
import {useAuth} from "../../hooks/useAuth";
import {useOrder} from "../../hooks/useOrder";
import {BaseLayout} from "../../layout/BaseLayout";
import {Header} from "../../layout/Header";
import {MenuItem} from "../../components/MenuItem";
import {ReactComponent as OrderIllustration} from "../../components/order.svg";

export function OrderPage() {
    const {id} = useParams();
    const {header} = useAuth();
    const {removeItem, order} = useOrder();
    return <BaseLayout>
        <Header/>
        <div className="flex flex-col gap-12 mx-auto max-w-7xl w-full px-4 pt-6">
            <h1 className="text-4xl font-bold">Finish Order</h1>

            <div className="flex gap-8">
                <div className="flex flex-col w-full">
                    <h2 className="font-bold text-xl mb-4">Your Items</h2>
                    {order.map((item, idx) => <MenuItem key={item.id} {...item} icon="remove"
                                                        onClick={() => removeItem(idx)}/>)}
                </div>
                <OrderIllustration />
            </div>

        </div>
    </BaseLayout>;
}
