import {Rating} from "../rating/Rating";
import * as PropTypes from "prop-types";
import {TextField} from "../form/TextField";
import {Button} from "../buttons/Button";
import {useState} from "react";

/**
 * Review box react component
 * @param data
 * @returns {JSX.Element}
 * @constructor
 */
export function ReviewForm({onSubmit}) {
    const [rating, setRating] = useState(0);
    const [comment, setComment] = useState("");

    function handleSubmit(e){
        e.preventDefault();

        if(rating === 0){
            setRatingError(true)
        }

        if(comment === ""){
            setCommentError(true)
        }

        if(comment && rating) {
            onSubmit({comment, rating})
        }
    }

    return <form onSubmit={handleSubmit} className="border rounded-md p-4 flex flex-col w-full gap-2">
        <p>Have any thoughts? Share it with us! ðŸ˜Š</p>
        <Rating value={rating} onChange={setRating} />
        <div className="flex gap-2">
            <TextField value={comment} onChange={setComment} placeholder="Review" />
            <Button variant="primary" type="submit">Send</Button>
        </div>
    </form>
}

ReviewForm.propTypes = {
    onSubmit: PropTypes.func
};
