import React from "react";
import { Link } from "react-router-dom";
import image from "../assets/restaurant.webp";

const Restaurant = ({ restaurant }) => {
  return (
    <div className="p-4 flex flex-col gap-2 bg-white shadow-lg border border-slate-200 rounded-md">
      <img
        src={image}
        className="max-w-full rounded-md shadow-md object-cover h-32"
      />

      <div className="flex items-center justify-between">
        <h2 className="text-xl font-bold">{restaurant.name}</h2>
      </div>

      <p className="text-slate-800 font-sm">{restaurant.description}</p>

      <Link
        className="text-purple-normal font-semibold hover:underline"
        to={`/restaurant/${restaurant.id}`}
      >
        Continue Â»
      </Link>
    </div>
  );
};

export default Restaurant;
