import axios from "axios";
import { useEffect, useState } from "react";
import Input from "../components/Input";
import MainLayout from "../components/layouts/MainLayout";
import { useDineEaseContext } from "../context/DineEaseContext";
import searchIcon from "../assets/Search Icon.svg";
import searchIconPurple from "../assets/Search Icon Purple.svg";
import ButtonSecondary from "../components/ButtonSecondary";
import Restaurant from "../components/Restaurant";

/**
 * Index page
 */
const Index = () => {
  const { user } = useDineEaseContext();
  const [restaurants, setRestaurants] = useState();

  /**
   * Fetch the restaurants on page load
   */
  useEffect(() => {
    axios
      .get("/search", { headers: { Authorization: `Bearer ${user.token}` } })
      .then((res) => {
        // generate 6 random indexes
        const restaurants = res.data;
        const indexes = [];

        for (let i = 0; i <= 6; i++) {
          let rand = Math.floor(Math.random() * restaurants.length);
          indexes.push(rand);
        }

        setRestaurants(restaurants.map((x) => indexes.includes(x.id)));
      });
  }, []);

  return (
    <MainLayout>
      <div className="flex items-stretch gap-2 justify-center mb-4">
        <Input
          placeholder="Search for restaurants"
          leftIcon={<img src={searchIcon} alt="Search Icon" />}
        />
        <div>
          <ButtonSecondary>
            <img src={searchIconPurple} alt="Search Icon" />
          </ButtonSecondary>
        </div>
      </div>

      <div className="grid grid-cols-3 gap-4">
        {restaurants?.map((r, i) => (
          <Restaurant key={i} restaurant={r} />
        ))}
      </div>
    </MainLayout>
  );
};

export default Index;
