import React, { useState } from "react";
import AuthLayout from "../layout/AuthLayout";
import { Link } from "react-router-dom";
import Input from "../components/ui/Input";
import lockClosed from "../assets/lock-closed.png";
import mail from "../assets/mail.png";
import Button from "../components/ui/Button";
import Alert from "../components/ui/Alert";

/**
 * Login Page
 */
const LoginPage = () => {
  const [email, setEmail] = useState("");

  return (
    <AuthLayout title="Sign in to DineEase">
      <h1 className="font-bold text-3xl">Sign in</h1>
      <p>
        You can{" "}
        <Link to="/register" className="font-semibold text-primary-dark">
          Register here!
        </Link>
      </p>

      <form className="mt-5 flex flex-col gap-4">
        <Alert>Error</Alert>

        <Input placeholder="Email" icon={<img src={mail} alt="Mail Icon" />} />
        <Input
          placeholder="Password"
          icon={<img src={lockClosed} alt="Lock Icon" />}
        />

        <Button className="mt-8">Log In</Button>
      </form>
    </AuthLayout>
  );
};

export default LoginPage;
