/**
 * React component for displaying the restaurant detail page
 * @constructor
 */
import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import axios from "axios";
import {useAuth} from "../../hooks/useAuth";
import {BaseLayout} from "../../layout/BaseLayout";
import {Header} from "../../layout/Header";
import {Rating} from "../../components/rating/Rating";
import {useOrder} from "../../hooks/useOrder";
import * as PropTypes from "prop-types";
import {Button} from "../../components/buttons/Button";

function MenuItem({onClick, data}) {
    return <div className="bg-white rounded-md border shadow-xl px-4 py-2 flex items-center justify-between w-full">
        <span className="font-semibold">{data.name}</span>

        <div className="flex gap-2 items-center">
            <span className="text-purple-500">{data.price.toFixed(2)} EUR</span>
            <Button variant="primary">Add</Button>
        </div>
    </div>
}

MenuItem.propTypes = {onClick: PropTypes.func, data: PropTypes.any};

export function RestaurantPage() {
    const {addItem} = useOrder();
    const {headers} = useAuth();
    const {id} = useParams();
    const [restaurant, setRestaurant] = useState(null);
    const [loading, setLoading] = useState(false);

    async function load(){
        setLoading(true)
        const result = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/restaurant/${id}`, {headers})
        setRestaurant(result.data);
        setLoading(false)
    }

    useEffect(() => {
        load()
    }, [id])

    if(!restaurant) return <span className="text-gray-700 italic text-center">Loading...</span>;

    return <BaseLayout>
        <Header logoColor="white"/>

        <div className="flex flex-col gap-8">
            <div className="w-screen h-[500px] relative">
                <div className="absolute inset-0 bg-gradient-to-b from-[rgba(0,0,0,.5)] to-transparent"></div>
                <img className="w-full h-full object-cover" src="/restaurant.webp" alt={restaurant?.name}/>
            </div>
            {
                loading && <span className="text-gray-700 italic text-center">Loading...</span>
            }
            <div className="mx-auto max-w-7xl w-full p-4 flex flex-col gap-3">
                <h1 className="font-bold text-6xl">{restaurant.name}</h1>
                <div className="flex flex-row gap-2 items-center">
                    <span className="text-gray-600">{restaurant.averageRating.toFixed(2)}</span> <Rating value={restaurant.averageRating} readOnly />
                </div>
                <p className="text-gray-600">
                    {restaurant.description}
                </p>

                <div className="flex flex-row justify-between">
                    <div className="flex flex-col gap-4 w-full">
                        <h2 className="text-xl font-bold">Menu</h2>
                        {
                            restaurant.menuItems.map(item => <MenuItem key={item.id} data={item} onClick={() => addItem(item)} />)
                        }
                    </div>
                    <div className="flex flex-col gap-4">
                        <h2 className="text-xl font-bold">Reviews</h2>
                    </div>
                </div>
            </div>
        </div>
    </BaseLayout>
}
