import {useState} from "react";
import {TextField} from "../../components/form/TextField";
import {ReactComponent as MailIcon} from "../../components/icons/mail.svg";
import {ReactComponent as PasswordIcon} from "../../components/icons/lock-closed.svg";
import {RoundedButton} from "../../components/Buttons/RoundedButton";
import {Link} from "react-router-dom";
import axios from "axios";
import {validateEmail} from "./utils";
import * as PropTypes from "prop-types";

function Alert({children}) {
    return <div className="py-3 px-5 rounded-xl border-red-700 text-red-700 bg-red-300">
        {children}
    </div>
}

Alert.propTypes = {children: PropTypes.node};

/**
 * Login form react component
 * @returns {null}
 * @constructor
 */
export function LoginForm() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    async function handleSubmit(e){
        e.preventDefault();

        if(!validateEmail(email)){
            setError('The email address is not valid');
            return;
        }

        const result = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/login`, {email, password})
        console.log(result)
    }

    return <div className="flex flex-col mt-24 gap-4 max-w-md w-full mx-auto">
        <h2 className="font-semibold text-3xl">
            Sign in
        </h2>
        <p className="mb-8">
            You can <Link className="font-bold text-purple-600">Register Here!</Link>
        </p>
        {error && <Alert>{error}</Alert> }
        <form className="flex flex-col gap-4" onSubmit={handleSubmit}>
            <TextField value={email} startIcon={<MailIcon />} onChange={setEmail} type="text" />
            <TextField value={password} startIcon={<PasswordIcon />} onChange={setPassword} type="password" />

            <div className="w-full flex flex-col mt-24">
                <RoundedButton variant="primary" type="submit">Login</RoundedButton>
            </div>
        </form>
    </div>
}
