/**
 * React component for displaying the order summary
 * @returns {null}
 * @constructor
 */
import {useOrder} from "../../hooks/useOrder";
import {BaseLayout} from "../../layout/BaseLayout";
import {Header} from "../../layout/Header";
import {MenuItem} from "../../components/menu/MenuItem";

export function OrderPage() {
    const {items, total, removeItem} = useOrder();
    return <BaseLayout>
        <Header />

        <div className="flex flex-col gap-8 w-full max-w-7xl pt-24 p-4">
            <h1 className="font-bold text-6xl">Finish Order</h1>

            <div className="flex flex-col gap-3">
                <h2 className="font-bold text-2xl">Your items</h2>
                {items.map((item, idx) => <MenuItem key={idx} data={item} onClick={() => removeItem(idx)} variant='remove' />)}
            </div>

            <span className="text-3xl">Total: {total.toFixed(2)} EUR</span>
        </div>
    </BaseLayout>
}
