import {BaseLayout} from "../../layout/BaseLayout";
import {Header} from "../../layout/Header";
import {TextField} from "../../components/form/TextField";
import {useEffect, useMemo, useState} from "react";
import {ReactComponent as SearchIcon} from "../../components/icons/search.svg";
import {Button} from "../../components/buttons/Button";
import axios from "axios";
import * as PropTypes from "prop-types";
import {useAuth} from "../../hooks/useAuth";

function Rating({readOnly, value}) {
    const [currentValue, setCurrentValue] = useState(value);

    useEffect(() => {
        setCurrentValue(value)
    }, [value]);

    const items = Array.from({length: 5})

    return <div className="flex gap-2">
        {
            items.map((_, idx) => <StarIcon onHover={() => setCurrentValue(idx+1)} active={idx < currentValue-1} key={idx} /> )
        }
    </div>
}

Rating.propTypes = {
    readOnly: PropTypes.bool,
    value: PropTypes.number
};

function RestaurantCard({data}) {
    return <div className={`bg-white shadow-xl rounded-md p-4 border group flex flex-col gap-2 min-h-[450px] ${data.visited ? 'border border-purple-400' : ''}`}>
        <img className="w-full h-48 object-cover rounded-lg shadow-lg group-hover:-translate-y-4 transition" src="/restaurant.webp" alt={data.name} />
        <div className="flex justify-between mt-4">
            <h2 className="font-bold text-xl">{data.name}</h2>

            <Rating readOnly value={data.averageRating} />
        </div>
        <p className="text-gray-600">{data.description}</p>

        <a href="#" className="mt-auto ml-auto font-bold text-purple-500 transition hover:bg-gray-100 rounded-lg p-2">Continue >></a>
    </div>
}

RestaurantCard.propTypes = {data: PropTypes.any};

/**
 * Search Page react component
 * @returns {null}
 * @constructor
 */
export function SearchPage() {
    const {headers} = useAuth();
    const [query, setQuery] = useState("");
    const [restaurants, setRestaurants] = useState([]);
    const [showMore, setShowMore] = useState(false);
    const visibleRestaurants = useMemo(() => showMore ? restaurants : restaurants.slice(0, 6), [showMore, restaurants]);

    async function fetchRestaurants() {
        const result = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/search?search=${query}`, {headers})
        console.log(result)
        setRestaurants(result.data.sort(() => Math.random() - 0.5));
    }

    function handleSearch(e) {
        e.preventDefault();

        fetchRestaurants();
    }

    return <BaseLayout>
        <Header logoColor="dark"/>

        <div className="flex flex-col items-center gap-8">
            <form onSubmit={handleSearch} className="flex flex-row gap-2 w-full max-w-md">
                <TextField value={query} onChange={setQuery} placeholder="Search for restaurants" type="search"
                           startIcon={<SearchIcon className="w-6 h-6 fill-gray-500"/>}/>
                <Button type="submit">
                    <SearchIcon className="w-8 h-8 fill-purple-500"/>
                </Button>
            </form>

            <div className="grid grid-cols-3 gap-8 w-full max-w-7xl mx-auto p-4 justify-center">
                {
                    visibleRestaurants.map(r => <RestaurantCard key={r.id} data={r} />)
                }
            </div>
        </div>
    </BaseLayout>
}
