import React, { useContext, useEffect } from "react";
import AuthContext from "../context/auth";

export const Header = ({ absolute, white, children }) => {
    const auth = useContext(AuthContext);

    return (
        <header className={`flex space-around items-center p-5 ${absolute ? "w-full absolute top-0 left-0 z-10" : ""}`}>
            <img src={white ? "/assets/Logo@3x-white.png" : "/assets/Logo@3x.png"} alt="logo" style={{
                height: "40px"
            }} />

            <div className="flex gap-3">
                {auth.token !== null && (
                    <button 
                        className="bg-slate-200 text-black p-3 rounded-md hover:bg-slate-300"
                        onClick={() => {
                            localStorage.clear();
                            window.location.href = "/";
                        }}
                    >
                        Logout
                    </button>
                )}
            </div>
        </header>
    );
}

const Footer = () => {
    return (
        <footer className="bg-slate-900 text-center p-3">
            <p className="text-white">All rights reserved. Shawormaa</p>
        </footer>
    );
}

const Layout = ({ children }) => {
    return (
        <>
            <main>
                {children}
            </main>
            <Footer />
        </>
    );
}

export default Layout;
