export default function DashboardPage() {
  var restaurants = [];

  useEffect(() => {
    var token = localStorage.getItem("token");

    instance(token)({
      method: "GET",
      url: "/search?query=",
    })
      .then(function (response) {
        restaurants = response.data;
      })
      .catch((e) => {
        alert("no");
      });
    return () => {};
  }, []);

  return (
    <div className="p-1">
      <div className="row space-between">
        <Logo></Logo>
        <button className="action-button">
          <img src={logoutImage.default} />
          Logout
        </button>
      </div>
      <div className="restaurant">
        <img src={Image.default} alt="" />
        <h2>Sarmale Bistro</h2>
        <p>
          Experience Romania's culinary heart at Sarmale Bistro. Savor classics
          with a modern touch in our intimate, authentic ambiance. Celebrate
          timeless tradition.
        </p>
        <button className="simple-button">{"View restaurant >>"}</button>
      </div>
    </div>
  );
}
