import {Logo} from "../../components/Logo";
import {Link} from "react-router-dom";
import {TextField} from "../../components/TextField";
import {ReactComponent as HeroIllustration} from "../../components/hero.svg";
import {ReactComponent as MailIcon} from "../../components/mail.svg";
import {ReactComponent as PasswordIcon} from "../../components/lock-closed.svg";
import {useState} from "react";
import {Button} from "../../components/Button";
import axios from "axios";

function LoginForm(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [error, setError] = useState("")

    async function handleSubmit(e) {
        e.preventDefault()
        setError("")

        try {

            const response = await axios.post("http://api.localhost/v1/login", {
                email,
                password
            })
            console.log(response.data);

        }catch (e) {
            setError(e.response.data.error)
        }
    }

    return <form onSubmit={handleSubmit} className="flex flex-col gap-4 mt-12">
        <TextField value={email} onChange={e => setEmail(e.target.value)} placeholder="Email"
                   icon={<MailIcon/>} fullWidth type="email" autoFocus/>
        <TextField value={password} onChange={e => setPassword(e.target.value)} placeholder="Password"
                   icon={<PasswordIcon/>} fullWidth type="password"/>

        <div className="flex flex-col gap-3 mt-16">
            <Button variant="primary" type="rounded" onClick={handleSubmit}>Login</Button>
        </div>
    </form>
}

export function LoginPage() {
    return <div className="flex w-screen h-screen overflow-hidden p-4">
        <div className="flex w-full flex-col h-full">
            <Logo/>
            <div className="flex flex-col p-8 mt-16 w-full items-center">
                <div className="flex flex-col max-w-sm w-full ">
                    <h2 className="text-2xl font-medium mb-2">Sign in</h2>
                    <p>You can <Link to="registration" className="font-bold text-purple-800">Register here!</Link></p>
                    <LoginForm />
                </div>
            </div>
        </div>
        <div className="w-full h-full bg-purple-800 rounded-xl flex flex-col justify-center items-center">
            <HeroIllustration className="w-96"/>
            <h1 className="text-2xl font-bold text-white text-center">Sign in to DineEase</h1>
        </div>
    </div>;
}
