import React from "react";

/**
 * Reuseable button component
 */
const Button = ({ children, fullWidth = true, className, ...rest }) => {
  return (
    <button
      className={`px-4 py-2 rounded-full bg-primary-dark text-white cursor-pointer hover:bg-primary-darker transition-all ${
        fullWidth ? "w-full" : "w-fit"
      } ${className}`}
      {...rest}
    >
      {children}
    </button>
  );
};

export default Button;
