import * as logoutImage from "../assets/logout.png";
import * as Image from "../assets/laxbw-prime-1715-hor-wide.webp";
import Logo from "../components/Logo.js";
import axios from "axios";
import { useEffect } from "react";
import { useLoaderData } from "react-router-dom";

const instance = (token) =>
  axios.create({
    baseURL: "http://api.localhost/v1/",
    timeout: 1000,
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });

var hasRun = false;
function justOnce() {
  if (hasRun) {
    return;
  }
  hasRun = true;

  var token = localStorage.getItem("token");

  instance(token)({
    method: "GET",
    url: "/search?query=",
  })
    .then(function (response) {
      return response.data;
    })
    .catch((e) => {
      console.log(e);
      return [];
    });
}

export default function DashboardPage() {
  const restaurants = useLoaderData();
  justOnce();

  return (
    <div className="p-1">
      <div className="row space-between">
        <Logo></Logo>
        <button className="action-button">
          <img src={logoutImage.default} />
          Logout
        </button>
      </div>
      {restaurants}
      <div className="restaurant">
        <img src={Image.default} alt="" />
        <h2>Sarmale Bistro</h2>
        <p>
          Experience Romania's culinary heart at Sarmale Bistro. Savor classics
          with a modern touch in our intimate, authentic ambiance. Celebrate
          timeless tradition.
        </p>
        <button className="simple-button">{"View restaurant >>"}</button>
      </div>
    </div>
  );
}
