import {Button} from "./Button";
import {ReactComponent as StarIcon} from "./star.svg";
import {useEffect, useState} from "react";

function Rating({readOnly, value, onChange}) {
    const [hovered, setHovered] = useState(value-1);

    useEffect(() => {
        setHovered(value-1)
    }, [value]);

    const stars = Array.from({length: 5})
    return <div className="inline-flex gap-1">
        {stars.map((_, idx) => <StarIcon
            onMouseEnter={() => !readOnly && setHovered(idx)}
            onClick={() => !readOnly && onChange(hovered)}
            className={hovered >= idx ? "fill-yellow-500" : "fill-gray-400"} key={idx}
        />)}
    </div>
}

export function RestaurantCard({imageSrc, name, rating, visited, description, onClick}) {
    return <div className={`p-4 pb-2 rounded-lg shadow-xl flex flex-col gap-2 w-96 h-[450px] ${visited ? 'border border-purple-500' : ''}`} onClick={onClick}>
        <img src={imageSrc} alt={name} className="w-full rounded-lg shadow-xl object-cover h-56"/>
        <div className="flex items-center justify-between mt-4">
            <h2 className="text-2xl font-bold">{name}</h2>
            <Rating readOnly value={rating} />
        </div>
        <p className="text-gray-600">{description}</p>
        <div className="ml-auto mt-auto -mr-3">
            <Button type="rounded" variant="text">
                Continue >>
            </Button>
        </div>
    </div>
}
