import React, { useContext, useEffect } from "react";
import { redirect, useLocation } from "react-router-dom";
import AuthContext from "../context/auth";

const Header = () => {
    const auth = useContext(AuthContext);
    useEffect(() => {
        console.log(window.location);
    })

    return (
        <header 
            className={`flex items-center p-5 ${auth.token === null || window.location.pathname.startsWith("/restaurant") ? "absolute top-0 left-0" : ""}`}
        >
            <img src="/assets/Logo@3x.png" alt="logo" style={{
                height: "40px"
            }} />

            {auth.token !== null && (
                <button 
                    className="ms-auto bg-slate-200 text-black p-3 rounded-md hover:bg-slate-300"
                    onClick={() => {
                        localStorage.clear();
                        window.location.href = "/";
                    }}
                >
                    Logout
                </button>
            )}
        </header>
    );
}

const Footer = () => {
    return (
        <footer className="bg-slate-900 text-center p-3">
            <p className="text-white">All rights reserved. Shawormaa</p>
        </footer>
    );
}

const Layout = ({ children }) => {
    return (
        <>
            <Header /> 
            <main>
                {children}
            </main>
            <Footer />
        </>
    );
}

export default Layout;
