import {useParams} from "react-router-dom";
import {BaseLayout} from "../../layout/BaseLayout";
import {useEffect, useState} from "react";
import axios from "axios";
import {useAuth} from "../../hooks/useAuth";
import {Header} from "../../layout/Header";
import {Rating} from "../../components/Rating";

export function RestaurantPage() {
    const {id} = useParams();
    const {header} = useAuth();

    const [restaurant, setRestaurant] = useState(null);

    async function load(){
        const response = await axios.get(`http://api.localhost/v1/restaurant/${id}`, {headers: {Authorization: header}})
        setRestaurant(response.data)
    }

    useEffect(() => {
        load()
    }, [])

    return <BaseLayout>
        <div className="relative">
            <div className="absolute z-10 top-0 left-0 right-0"><Header color="white" /></div>
            <div className="bg-gradient-to-b from-[#00000060] to-[#00000000] absolute top-0 bottom-0 left-0 right-0"></div>
            <img src="/restaurant.webp" className="h-64 w-full object-cover" />
        </div>
        <div className="flex flex-col gap-4 mx-auto max-w-7xl w-full px-4 pt-6">
            <h1 className="text-4xl font-bold">{restaurant.name}</h1>
            <div className="flex items-center gap-1">
                {restaurant.averageRating.toFixed(2)}
                <Rating value={restaurant.averageRating} />
            </div>
        </div>
    </BaseLayout>
}
