import React, { useContext, useEffect } from "react";
import { Link, redirect } from "react-router-dom";
import AuthContext from "../context/auth";

const Home = () => {
    const auth = useContext(AuthContext);
    
    if (auth.token !== null)
        return (
            <p>authenticated</p>
        );
    return (
        <div className="w-screen h-screen flex">
            <form className="flex flex-col justify-between px-10 py-32 text-center" style={{ width: "80%" }}>
                <div className="text-center">
                    <h2 className="text-3xl font-bold mb-2">Sign in</h2>
                    <p >You can <Link to={"/register"}>Register here!</Link></p>
                </div>

                <div>
                    <input type={"email"} required name="email" placeholder="Email" className="form-input" />
                    <input type={"password"} required name="password" placeholder="Password" className="form-input" />
                </div>

                <button 
                    type="submit" 
                    className="bg-purple-800 text-white p-3 rounded-md mx-auto hover:outline-2 hover:bg-purple-600"
                    style={{ width: "60%" }}
                > 
                    Login
                </button>
            </form>
            <div className="flex flex-col justify-center items-center bg-purple-800 w-full">
                <img src="/assets/Wavy_F_D-01_Single-11 2@3x.png" alt="man eating" style={{ height: "400px" }}></img>
                <h3 className="text-3xl font-bold text-white">Sign in to DineEase</h3>
            </div>
        </div>
    );
}

export default Home;
