import React from "react";
import Button from "./Button";

/**
 * Popup that pops up on success order
 */
const SuccessOrderPopup = ({ isOpen, onClose, onAction }) => {
  if (!isOpen) return null;

  return (
    <div className="flex flex-col gap-2 w-96 z-10 bg-white absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 shadow-md border border-slate-200 rounded-md px-4 py-2">
      <h2 className="text-xl font-bold text-center">Successful order!</h2>
      <p className="text-center text-slate-800">
        Your order has been sent successfully.
      </p>

      <Button onClick={onAction}>Go Back</Button>
    </div>
  );
};

export default SuccessOrderPopup;
