import {Header} from "../../layout/Header";
import {TextField} from "../../components/TextField";
import {useEffect, useState} from "react";
import {ReactComponent as SearchIcon} from "../../components/search.svg";
import {Button} from "../../components/Button";
import axios from "axios";
import {useAuth} from "../../hooks/useAuth";
import {RestaurantCard} from "../../components/RestaurantCard";

export function SearchPage() {
    const [query, setQuery] = useState("");
    const [results, setResults] = useState([]);
    const [showMore, setShowMore] = useState(false);
    const {token} = useAuth();
    const RESULT_LIMIT = 6;

    async function search(){
        const restaurants = await axios.get(`http://api.localhost/v1/search?query=${query}`, {headers: {Authorization: `Bearer ${token}`}})
        setResults(restaurants.data.sort(() => Math.random() - 0.5))
    }

    useEffect(() => {
        void search()
    }, [])

    return <div className="flex flex-col w-full">
        <Header />

        <main className="flex flex-col mt-8 justify-center w-full">
            <div className="flex gap-2 max-w-sm w-full">
                <TextField fullWidth icon={<SearchIcon className="fill-gray-500"/>} placeholder="Search for restaurants" onChange={e => setQuery(e.target.value)} value={query} type="search" />
                <Button variant="secondary" onClick={() => search()}><SearchIcon className="fill-purple-500" /></Button>
            </div>

            <div className="flex flex-wrap">
                {results.slice(0, showMore ? Number.MAX_SAFE_INTEGER : RESULT_LIMIT).map(r => <RestaurantCard key={r.id} {...r} />)}
            </div>
        </main>
    </div>
}
